onClick:
(async () => {
  // **Check if inputs are empty first**
  if (!Input_Email.text || !Input_Password.text) {
    showAlert('Please enter both Email and Password!', 'error');
    return; // Stop the function here
  }

  try {
    // Run the query only if inputs are not empty
    const userResult = await check_User.run();

    if (userResult && userResult.length > 0) {
      // User found
      const userData = userResult[0];
      await storeValue('userRole', userData.Role);
      await storeValue('userArea', userData.Assigned_Area);
      await storeValue('userUnit', userData.Assigned_Unit);
      navigateTo('Dashboard');
    } else {
      // No user found
      showAlert('Invalid Email or Password!', 'error');
    }
  } catch (error) {
    // Handle query errors
    console.error("Login failed:", error);
    showAlert('Login failed. Please try again.', 'error');
  }
})();
