{{(async () => {
  try {
    // Run the query to check email and password
    const userResult = await check_User.run();

    // Check if the query returned any data (i.e., if a user was found)
    if (userResult && userResult.length > 0) {
      // User found, login successful
      const userData = userResult[0]; // Get the first (and only) user found

      // Store user details in Appsmith's store
      await storeValue('userRole', userData.Role);
      await storeValue('userArea', userData.Assigned_Area);
      await storeValue('userUnit', userData.Assigned_Unit);

      // Navigate to the Dashboard page
      navigateTo('Dashboard'); // Make sure you have a page named 'Dashboard'

    } else {
      // No user found with that email/password combination
      showAlert('Invalid Email or Password!', 'error');
    }
  } catch (error) {
    // Handle any errors during the query execution
    console.error("Login failed:", error); // Log the error for debugging
    showAlert('Login failed. Please try again.', 'error');
  }
})();}}
